import React from 'react';
import { scryRenderedComponentsWithType } from 'react-dom/test-utils';

const Work = () => {
  const cards = [
    {
      name: 'Instagram',
      description: 'Explore Page Case Study',
    },
    {
      name: 'Children and Screens',
      description: 'Logo Redesign',
    },
  ];
  return (
    <div id='work' style={{ display: 'flex', justifyContent: 'space-around' }}>
      {cards.map((card) => {
        return (
          <div
            className='work-card'
            style={{
              height: '300px',
              width: '300px',
              borderStyle: 'solid',
              borderColor: 'black',
              // borderRadius: '5',
              textAlign: 'center',
            }}
          >
            <h3>{card.name}</h3>
            <h4>{card.description}</h4>
          </div>
        );
      })}
    </div>
  );
};

export default Work;
